        volume24h: 48400 + Math.random() * 5000
      };

      setPriceData(mockPrice);
    } catch (error) {
      console.log('Price fetch simulation running');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    // Initial fetch
