  // Get available wallets
  const availableExternalWallets = multiWallet.getAvailableExternalWallets();
  const availableWallets: WalletType[] = ['self-contained', ...availableExternalWallets];

  // Copy wallet address to clipboard
  const copyAddress = async () => {
    if (!multiWallet.address) return;

    try {
      await navigator.clipboard.writeText(multiWallet.address);
      toast({
        title: "Address Copied",
        description: "Wallet address copied to clipboard",
      });
    } catch (error) {
      console.error('Failed to copy address:', error);
    }
  };

  // View wallet on Solscan
  const viewOnSolscan = () => {
    if (!multiWallet.address) return;
    window.open(`${SOLSCAN_BASE_URL}/account/${multiWallet.address}`, '_blank');
  };

  // Handle wallet selection
  const handleWalletSelect = async (walletType: WalletType) => {
    if (selectedWallet === walletType) {
      setIsOpen(false);
      return;
    }

    try {
      if (walletType === 'self-contained') {
        await multiWallet.switchToSelfContained();
        toast({
          title: "Wallet Switched",
          description: "Switched to self-contained wallet",
        });
      } else {
        await multiWallet.switchToExternalWallet(walletType as ExternalWalletType);
        toast({
          title: "Wallet Connected",
          description: `Connected to ${walletOptions.find(w => w.type === walletType)?.name}`,
        });
      }
      setIsOpen(false);
    } catch (error) {
      toast({
        title: "Connection Failed",
        description: `Failed to connect to ${walletOptions.find(w => w.type === walletType)?.name}. Please make sure the wallet extension is installed and unlocked.`,
        variant: "destructive",
      });
    }
  };

  const currentWallet = walletOptions.find(w => w.type === selectedWallet);

  if (!multiWallet.connected) {
    return (
      <Button 
        variant="outline" 
        disabled={multiWallet.connecting}
        className="bg-galaxy-button border-galaxy-purple/30 text-white"
      >
        {multiWallet.connecting ? 'Connecting...' : 'Wallet Loading...'}
      </Button>
    );
  }

  return (
    <DropdownMenu open={isOpen} onOpenChange={setIsOpen}>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="outline"
          className="bg-galaxy-card border-galaxy-purple/30 hover:border-galaxy-blue/50 text-galaxy-bright"
        >
          <span className="mr-2">{currentWallet?.icon}</span>
          <span className="hidden sm:inline">
            {multiWallet.address ? `${multiWallet.address.slice(0, 4)}...${multiWallet.address.slice(-4)}` : 'Wallet'}
          </span>
          <span className="sm:hidden">Wallet</span>
          <ChevronDown className="w-4 h-4 ml-2" />
        </Button>
      </DropdownMenuTrigger>

      <DropdownMenuContent 
        align="end" 
        className="w-80 bg-galaxy-card border-galaxy-purple/30"
      >
        {/* Current Wallet Info */}
        <div className="p-4 space-y-3">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium text-galaxy-bright">Connected Wallet</span>
            <div className="flex items-center space-x-1">
              <div className="w-2 h-2 bg-green-500 rounded-full" />
              <span className="text-xs text-green-400">Active</span>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-xs text-galaxy-accent">Current:</span>
              <div className="flex items-center space-x-2">
                <span className="text-xs">{currentWallet?.icon}</span>
                <span className="text-xs text-galaxy-bright">{currentWallet?.name}</span>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-xs text-galaxy-accent">Address:</span>
              <div className="flex items-center space-x-2">
                <span className="text-xs font-mono text-galaxy-bright">
                  {multiWallet.address ? `${multiWallet.address.slice(0, 8)}...${multiWallet.address.slice(-8)}` : 'N/A'}
                </span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={copyAddress}
                  className="h-6 w-6 p-0 hover:bg-galaxy-purple/20"
                  disabled={!multiWallet.address}
                >
                  <Copy className="w-3 h-3" />
                </Button>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-xs text-galaxy-accent">Balance:</span>
              <span className="text-xs font-medium text-galaxy-bright">
                {multiWallet.balance.toFixed(4)} SOL
              </span>
            </div>
          </div>
        </div>

        <DropdownMenuSeparator className="bg-galaxy-purple/30" />

        {/* Wallet Selection */}
        <DropdownMenuLabel className="text-galaxy-bright px-4">Select Wallet</DropdownMenuLabel>

        <div className="p-2 space-y-1">
          {walletOptions.map((wallet) => (
            <DropdownMenuItem
              key={wallet.type}
              onClick={() => handleWalletSelect(wallet.type)}
              className={`
                text-galaxy-bright hover:bg-galaxy-purple/20 cursor-pointer p-3 rounded-md
                ${selectedWallet === wallet.type ? 'bg-galaxy-purple/30' : ''}
                ${!availableWallets.includes(wallet.type) ? 'opacity-50' : ''}
              `}
              disabled={!availableWallets.includes(wallet.type)}
            >
              <div className="flex items-center justify-between w-full">
                <div className="flex items-center space-x-3">
                  <span className="text-lg">{wallet.icon}</span>
                  <div>
                    <div className="flex items-center space-x-2">
                      <span className="font-medium">{wallet.name}</span>
                      {selectedWallet === wallet.type && (
                        <Check className="w-4 h-4 text-green-400" />
                      )}
                    </div>
                    <span className="text-xs text-galaxy-accent">{wallet.description}</span>
                  </div>
                </div>
                {!availableWallets.includes(wallet.type) && (
                  <span className="text-xs text-galaxy-accent">Not Detected</span>
                )}
              </div>
            </DropdownMenuItem>
          ))}
        </div>

        <DropdownMenuSeparator className="bg-galaxy-purple/30" />

        {/* Wallet Actions */}
        <div className="p-2">
          <DropdownMenuItem 
            onClick={copyAddress}
            className="text-galaxy-bright hover:bg-galaxy-purple/20 cursor-pointer"
          >
            <Copy className="w-4 h-4 mr-2" />
            Copy Address
          </DropdownMenuItem>

          <DropdownMenuItem 
            onClick={viewOnSolscan}
            className="text-galaxy-bright hover:bg-galaxy-purple/20 cursor-pointer"
          >
            <ExternalLink className="w-4 h-4 mr-2" />
            View on Solscan
          </DropdownMenuItem>
        </div>

        {/* Network Info */}
        <DropdownMenuSeparator className="bg-galaxy-purple/30" />
        <div className="p-4">
          <div className="text-center">
            <div className="inline-flex items-center px-3 py-1 bg-green-500/20 rounded-full border border-green-500/30">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse mr-2" />
              <span className="text-xs font-medium text-green-400">Solana Mainnet</span>
            </div>
          </div>
        </div>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/real-send-tab.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent } from '@/components/ui/card';
import { useExternalWallets } from '@/hooks/use-external-wallets';
import { useExternalWalletBalances } from '@/hooks/use-external-wallet-balances';
import { useInstantBalance } from '@/hooks/use-instant-balance';
import { TransactionSuccessModal } from './transaction-success-modal';
import { PublicKey } from '@solana/web3.js';
import { useToast } from '@/hooks/use-toast';
import { solscanTracker } from '@/lib/solscan-tracker';
import { GoldTokenService } from '@/services/gold-token-service';

export function RealSendTab() {
  const [selectedToken, setSelectedToken] = useState<'SOL' | 'GOLD'>('SOL');
  const [recipientAddress, setRecipientAddress] = useState('');
  const [amount, setAmount] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [completedTransaction, setCompletedTransaction] = useState<{
    type: 'send';
    amount: number;
    token: string;
    recipient: string;
    txSignature: string;
  } | null>(null);

  const wallet = useExternalWallets();
  const { data: balances } = useExternalWalletBalances();
  const instantBalance = useInstantBalance();
  const { toast } = useToast();

  // Use INSTANT balance that updates immediately when switching wallets
  const selectedBalance = selectedToken === 'SOL' ? 
    instantBalance.balance : 
    (balances?.gold || 0);

  // Display current wallet info for transparency
  const walletInfo = wallet.connected && wallet.address ? 
    `${wallet.selectedWallet} - ${wallet.address.slice(0, 8)}...` : 
    'Not connected';

  const handleMaxClick = () => {
    if (selectedToken === 'SOL') {
      // Leave some SOL for transaction fees
      const maxAmount = Math.max(0, selectedBalance - 0.001);
      setAmount(maxAmount.toString());
    } else {
      setAmount(selectedBalance.toString());
    }
  };

  const isValidAddress = (address: string): boolean => {
    try {
      new PublicKey(address);
      return true;
    } catch {
      return false;
    }
  };

  // REAL send transaction using actual wallet balance
  const handleSend = async () => {
    if (!isValidAddress(recipientAddress) || !amount || Number(amount) <= 0) {
      toast({
        title: "Invalid Input",
        description: "Please enter a valid recipient address and amount",
        variant: "destructive"
      });
      return;
    }

    const sendAmount = Number(amount);

    // Check REAL balance including transaction fees
    const feeBuffer = selectedToken === 'SOL' ? 0.001 : 0;
    const totalRequired = sendAmount + feeBuffer;

    if (selectedToken === 'SOL' && totalRequired > selectedBalance) {
      toast({
        title: "Insufficient Balance",
        description: `Need ${totalRequired.toFixed(6)} SOL (including fees) but only have ${selectedBalance.toFixed(6)} SOL`,
        variant: "destructive"
      });
      return;
    } else if (selectedToken === 'GOLD' && sendAmount > selectedBalance) {
      toast({
        title: "Insufficient Balance", 
        description: `Insufficient GOLD balance. You have ${selectedBalance.toFixed(4)} GOLD`,
        variant: "destructive"
      });
      return;
    }

    if (!wallet.connected || !wallet.address) {
      toast({
        title: "Wallet Required",
        description: "Please connect your wallet to send tokens",
        variant: "destructive"
      });
      return;
    }

    setIsSending(true);

    try {
      console.log(`Creating REAL send transaction: ${sendAmount} ${selectedToken} to ${recipientAddress}`);

      // Get wallet instance for REAL transaction
      const walletInstance = (window as any).phantom?.solana || (window as any).solflare || (window as any).trustwallet?.solana;
      if (!walletInstance) {
        throw new Error('Wallet not found');
      }

      // Create REAL blockchain transaction
      const { Connection, PublicKey, Transaction, SystemProgram, LAMPORTS_PER_SOL } = await import('@solana/web3.js');
      const connection = new Connection('https://solana.publicnode.com');

      const transaction = new Transaction();

      if (selectedToken === 'SOL') {
        // REAL SOL transfer using actual wallet balance
        transaction.add(
          SystemProgram.transfer({
            fromPubkey: new PublicKey(wallet.address),
            toPubkey: new PublicKey(recipientAddress),
            lamports: sendAmount * LAMPORTS_PER_SOL,
          })
        );
      } else {
        // GOLD token transfer using SPL token service
        console.log('Creating REAL GOLD token transfer...');
        const goldService = new GoldTokenService();

        const signature = await goldService.transferGold(
          walletInstance, 
          recipientAddress, 
          sendAmount
        );

        // Set transaction details for success modal
        setCompletedTransaction({
          type: 'send',
          amount: sendAmount,
          token: selectedToken,
          recipient: recipientAddress,
          txSignature: signature
        });
        setShowSuccessModal(true);

        // Clear form
        setRecipientAddress('');
        setAmount('');

        toast({
          title: "GOLD Transaction Sent!",
          description: `Successfully sent ${sendAmount} GOLD to ${recipientAddress.slice(0, 8)}...`,
        });

        console.log(`ðŸŽ‰ REAL GOLD send successful: ${signature}`);
        return; // Exit early since goldService handles everything
      }

      // Get recent blockhash for REAL transaction
      const { blockhash } = await connection.getLatestBlockhash();
      transaction.recentBlockhash = blockhash;
      transaction.feePayer = new PublicKey(wallet.address);

      console.log('Requesting wallet signature for REAL transaction...');

      // Sign and send REAL transaction with actual wallet
      const signedTransaction = await walletInstance.signTransaction(transaction);
      const signature = await connection.sendRawTransaction(signedTransaction.serialize());

      console.log(`REAL transaction sent: ${signature}`);

      // Track transaction for Solscan
      solscanTracker.trackTransaction({
        signature,
        type: 'send',
        token: selectedToken,
        amount: sendAmount
      });

      // Show contract address info 
      solscanTracker.showContractInfo(selectedToken);

      // Wait for confirmation on blockchain
      await connection.confirmTransaction(signature);

      console.log('ðŸ”— View on Solscan:', solscanTracker.getSolscanUrl(signature));

      // Set REAL transaction details for success modal
      setCompletedTransaction({
        type: 'send',
        amount: sendAmount,
        token: selectedToken,
        recipient: recipientAddress,
        txSignature: signature
      });
      setShowSuccessModal(true);

      // Clear form
      setRecipientAddress('');
      setAmount('');

      toast({
        title: "Transaction Sent!",
        description: `Successfully sent ${sendAmount} ${selectedToken} to ${recipientAddress.slice(0, 8)}...`,
      });

      console.log(`REAL send successful: ${signature}`);

    } catch (error: any) {
      console.error('REAL send failed:', error);

      let errorMessage = error.message;
      if (errorMessage?.includes('User rejected')) {
        errorMessage = 'Transaction was cancelled by user';
      } else if (errorMessage?.includes('insufficient funds')) {
        errorMessage = 'Insufficient balance for this transaction';
      }

      toast({
        title: "Send Failed",
        description: errorMessage || "Transaction failed. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsSending(false);
    }
  };

  return (
    <div className="max-w-md mx-auto space-y-6">
      {/* Token Selection */}
      <div className="space-y-3">
        <label className="text-sm font-medium text-galaxy-bright">Send Token</label>
        <div className="grid grid-cols-2 gap-2">
          {(['SOL', 'GOLD'] as const).map((token) => (
            <Button
              key={token}
              variant={selectedToken === token ? "default" : "outline"}
              onClick={() => setSelectedToken(token)}
              className="text-sm"
            >
              {token}
            </Button>
          ))}
        </div>
      </div>

      {/* Balance Display */}
      <Card className="bg-galaxy-card border-galaxy-purple/20">
        <CardContent className="p-4">
          <div className="text-center space-y-2">
            <p className="text-sm text-galaxy-muted">Available Balance</p>
            <p className="text-2xl font-bold text-galaxy-bright">
              {selectedBalance.toFixed(selectedToken === 'SOL' ? 6 : 4)} {selectedToken}
            </p>
            {wallet.connected && selectedToken === 'SOL' && (
              <p className="text-xs text-galaxy-muted">
                {walletInfo}
              </p>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Recipient Address */}
      <div className="space-y-3">
        <label className="text-sm font-medium text-galaxy-bright">Recipient Address</label>
        <Input
          placeholder="Enter Solana wallet address"
          value={recipientAddress}
          onChange={(e) => setRecipientAddress(e.target.value)}
          className="bg-white border-galaxy-purple/30 text-black placeholder:text-gray-500"
        />
      </div>

      {/* Amount */}
      <div className="space-y-3">
        <label className="text-sm font-medium text-galaxy-bright">Amount</label>
        <div className="relative">
          <Input
            type="number"
            placeholder={`Enter ${selectedToken} amount`}
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="bg-white border-galaxy-purple/30 text-black placeholder:text-gray-500 pr-16"
            step={selectedToken === 'SOL' ? '0.000001' : '0.0001'}
          />
          <Button
            variant="ghost"
            size="sm"
            onClick={handleMaxClick}
            className="absolute right-2 top-1/2 transform -translate-y-1/2 text-xs text-galaxy-accent hover:text-galaxy-bright"
          >
            MAX
          </Button>
        </div>
      </div>

      {/* Send Button */}
      <Button
        onClick={handleSend}
        disabled={!wallet.connected || !recipientAddress || !amount || isSending || Number(amount) <= 0 || Number(amount) > selectedBalance}
        className="w-full bg-galaxy-accent hover:bg-galaxy-accent/80 text-white font-semibold"
      >
        {isSending ? 'Sending...' : `Send ${selectedToken}`}
      </Button>

      {/* Connection Status */}
      {!wallet.connected && (
        <p className="text-center text-sm text-galaxy-muted">
          Connect your wallet to send tokens
        </p>
      )}

      {/* Success Modal */}
      {showSuccessModal && completedTransaction && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-galaxy-card p-6 rounded-lg border border-galaxy-purple/20 max-w-md w-full mx-4">
            <h3 className="text-lg font-bold text-galaxy-bright mb-4">Transaction Successful!</h3>
            <p className="text-galaxy-muted mb-4">
              Successfully sent {completedTransaction.amount} {completedTransaction.token}
            </p>
            <div className="flex space-x-2">
              <Button
                variant="outline"
                onClick={() => window.open(`https://explorer.solana.com/tx/${completedTransaction.txSignature}`, '_blank')}
                className="flex-1"
              >
                View on Explorer
              </Button>
              <Button
                onClick={() => setShowSuccessModal(false)}
                className="flex-1"
              >
                Close
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/real-staking-tab.t.content:
import React, { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { ExternalLink } from 'lucide-react';
import { useExternalWallets } from '@/hooks/use-external-wallets';
import { useInstantBalance } from '@/hooks/use-instant-balance';
import { useToast } from '@/hooks/use-toast';
import { solscanTracker } from '@/lib/solscan-tracker';
import { STAKING_APY, SOLSCAN_BASE_URL } from '@/lib/constants';
import { realStakingService } from '@/lib/real-staking-service';
import { GoldTokenService } from '@/services/gold-token-service';

export function RealStakingTab() {
  const wallet = useExternalWallets();
  const instantBalance = useInstantBalance();
  const { toast } = useToast();

  const [stakeAmount, setStakeAmount] = useState('');
  const [unstakeAmount, setUnstakeAmount] = useState('');
  const [isStaking, setIsStaking] = useState(false);
  const [isUnstaking, setIsUnstaking] = useState(false);
  const [lastTxId, setLastTxId] = useState<string | null>(null);

  // Get REAL staking info from connected wallet
  const stakingInfo = wallet.connected && wallet.address ? realStakingService.getStakingInfo(wallet.address) : [];
  const totalStaked = wallet.connected && wallet.address ? realStakingService.getTotalStaked(wallet.address) : 0;
  const pendingRewards = wallet.connected && wallet.address ? realStakingService.calculateRewards(wallet.address) : 0;

  // REAL stake using actual SOL from wallet
  const handleStake = async () => {
    if (!wallet.connected || !stakeAmount) {
      toast({
        title: "Invalid Input",
        description: "Please connect wallet and enter an amount to stake",
        variant: "destructive"
      });
      return;
    }

    // Use precise calculation to avoid floating point issues
    const amount = Math.floor(Number(stakeAmount) * 1000000) / 1000000;
    const availableBalance = instantBalance.balance;
    const feeBuffer = 0.001; // Reserve for transaction fees

    if (amount <= 0 || (amount + feeBuffer) > availableBalance) {
      toast({
        title: "Invalid Amount", 
        description: availableBalance === 0 ? 
          "You need SOL to stake. Your wallet balance is 0." :
          `Please enter a valid amount (max: ${(availableBalance - feeBuffer).toFixed(6)} SOL)`,
        variant: "destructive"
      });
      return;
    }

    setIsStaking(true);

    try {
      console.log(`Executing REAL stake: ${amount} SOL from wallet ${wallet.address}`);

      // Get wallet instance for REAL transaction
      const walletInstance = (window as any).phantom?.solana || (window as any).solflare || (window as any).trustwallet?.solana;
      if (!walletInstance) {
        throw new Error('Wallet not found');
      }

      if (!wallet.address) {
        throw new Error('Wallet address not found');
      }

      const result = await realStakingService.stakeSOL(amount, wallet.address, walletInstance);

      if (result.success && result.signature) {
        setLastTxId(result.signature);
        setStakeAmount('');

        // Track transaction for Solscan
        solscanTracker.trackTransaction({
          signature: result.signature,
          type: 'stake',
          token: 'SOL',
          amount
        });

        console.log('ðŸ”— Staking Transaction on Solscan:', solscanTracker.getSolscanUrl(result.signature));

        toast({
          title: "Staking Successful! ðŸ”’",
          description: (
            <div className="space-y-2">
              <p>Your SOL has been staked successfully!</p>
              <div className="flex items-center space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => window.open(`${SOLSCAN_BASE_URL}/tx/${result.signature}`, '_blank')}
                >
                  View on Solscan <ExternalLink className="w-3 h-3 ml-1" />
                </Button>
              </div>
            </div>
          ),
        });

      } else {
        throw new Error(result.error || 'Staking failed');
      }

    } catch (error: any) {
      console.error('REAL stake failed:', error);
      toast({
        title: "Staking Failed",
        description: error.message || "Transaction failed. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsStaking(false);
    }
  };

  // REAL unstake
  const handleUnstake = async () => {
    if (!wallet.connected || !unstakeAmount) {
      toast({
        title: "Invalid Input",
        description: "Please connect wallet and enter an amount to unstake",
        variant: "destructive"
      });
      return;
    }

    const amount = Number(unstakeAmount);
    if (amount <= 0 || amount > totalStaked) {
      toast({
        title: "Invalid Amount",
        description: totalStaked === 0 ? 
          "You have no staked SOL to unstake." :
          `Please enter a valid amount (max: ${totalStaked.toFixed(6)} SOL)`,
        variant: "destructive"
      });
      return;
    }

    setIsUnstaking(true);

    try {
      console.log(`Executing REAL unstake: ${amount} SOL for wallet ${wallet.address}`);

      // Get wallet instance
      const walletInstance = (window as any).phantom?.solana || (window as any).solflare || (window as any).trustwallet?.solana;

      if (!wallet.address) {
        throw new Error('Wallet address not found');
      }

      const result = await realStakingService.unstakeSOL(amount, wallet.address, walletInstance);

      if (result.success && result.signature) {
        setLastTxId(result.signature);
        setUnstakeAmount('');

        toast({
          title: "Unstaking Successful! ðŸ”“",
          description: `Successfully unstaked ${amount} SOL`,
        });

      } else {
        throw new Error(result.error || 'Unstaking failed');
      }

    } catch (error: any) {
      console.error('REAL unstake failed:', error);
      toast({
        title: "Unstaking Failed",
        description: error.message || "Transaction failed. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsUnstaking(false);
    }
  };

  return (
    <div className="max-w-md mx-auto space-y-6">
      {/* Staking Overview */}
      <Card className="bg-gradient-to-br from-galaxy-purple/10 to-galaxy-accent/5 border-galaxy-purple/20">
        <CardContent className="p-6">
          <div className="text-center space-y-4">
            <h3 className="text-lg font-semibold text-galaxy-bright">SOL Staking</h3>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-1">
                <p className="text-sm text-galaxy-muted">Total Staked</p>
                <p className="text-xl font-bold text-galaxy-bright">
                  {totalStaked.toFixed(6)} SOL
                </p>
              </div>
              <div className="space-y-1">
