import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';

interface WalletOption {
  name: string;
  icon: string;
  description: string;
  installed: boolean;
  connect: () => Promise<void>;
}

interface WalletSelectorProps {
  onConnect: (walletName: string) => Promise<void>;
  connecting: boolean;
  connected: boolean;
  currentWallet?: string;
}

export function WalletSelector({ onConnect, connecting, connected, currentWallet }: WalletSelectorProps) {
  const [open, setOpen] = useState(false);
  const [selectedWallet, setSelectedWallet] = useState<string | null>(null);

  const wallets: WalletOption[] = [
    {
      name: 'Phantom',
      icon: 'ðŸ‘»',
      description: 'Connect using Phantom wallet',
      installed: !!(window as any).solana?.isPhantom,
      connect: async () => {
        if ((window as any).solana?.isPhantom) {
          await onConnect('Phantom');
          setOpen(false);
        } else {
          window.open('https://phantom.app/', '_blank');
        }
      }
    },
    {
      name: 'Solflare',
      icon: 'ðŸ”¥',
      description: 'Connect using Solflare wallet',
      installed: !!(window as any).solflare?.isSolflare,
      connect: async () => {
        if ((window as any).solflare?.isSolflare) {
          await onConnect('Solflare');
          setOpen(false);
        } else {
          window.open('https://solflare.com/', '_blank');
        }
      }
    },
    {
      name: 'Backpack',
      icon: 'ðŸŽ’',
      description: 'Connect using Backpack wallet',
      installed: !!(window as any).backpack?.isBackpack,
      connect: async () => {
        if ((window as any).backpack?.isBackpack) {
          await onConnect('Backpack');
          setOpen(false);
        } else {
          window.open('https://backpack.app/', '_blank');
        }
      }
    },
    {
      name: 'Trust Wallet',
      icon: 'ðŸ›¡ï¸',
      description: 'Connect using Trust Wallet',
      installed: !!(window as any).trustwallet || 
                  !!(window as any).trust ||
                  (!!(window as any).solana && ((window as any).solana.isTrust || (window as any).solana.isTrustWallet)),
      connect: async () => {
        let hasAdapter = false;

        // Check various Trust Wallet injection methods
        if ((window as any).trustwallet || 
            (window as any).trust || 
            ((window as any).solana?.isTrust) || 
            ((window as any).solana?.isTrustWallet)) {
          hasAdapter = true;
        }

        if (hasAdapter) {
          await onConnect('Trust Wallet');
          setOpen(false);
        } else {
          window.open('https://trustwallet.com/', '_blank');
        }
      }
    }
  ];

  if (connected) {
    return (
      <div className="flex items-center space-x-2">
        <span className="text-sm text-gray-300">Connected: {currentWallet}</span>
        <Button 
          onClick={() => onConnect('disconnect')}
          className="bg-red-600 hover:bg-red-700 text-white"
          size="sm"
        >
          Disconnect
        </Button>
      </div>
    );
  }

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button 
          className="bg-blue-gradient hover:from-blue-600 hover:to-blue-700 text-white font-medium px-6 py-2 rounded-lg transition-all duration-200 transform hover:scale-105 shadow-lg"
          disabled={connecting}
        >
          {connecting ? 'Connecting...' : 'Connect Wallet'}
        </Button>
      </DialogTrigger>
      <DialogContent className="bg-defi-secondary border-defi-accent max-w-md">
        <DialogHeader>
          <DialogTitle className="text-white text-center text-xl font-bold">
            Connect Your Wallet
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-3 mt-6">
          {wallets.map((wallet) => (
            <Card 
              key={wallet.name}
              className={`bg-defi-accent/50 border-defi-accent hover:border-blue-primary/60 transition-all duration-200 cursor-pointer ${
                selectedWallet === wallet.name ? 'border-blue-primary' : ''
              }`}
              onClick={() => setSelectedWallet(wallet.name)}
            >
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="text-2xl">{wallet.icon}</div>
                    <div>
                      <h3 className="font-medium text-white">{wallet.name}</h3>
                      <p className="text-sm text-gray-400">{wallet.description}</p>
                    </div>
                  </div>
                  <div className="flex flex-col items-end space-y-2">
                    {wallet.installed ? (
                      <span className="text-xs text-green-400 bg-green-400/20 px-2 py-1 rounded">
                        Installed
                      </span>
                    ) : (
                      <span className="text-xs text-orange-400 bg-orange-400/20 px-2 py-1 rounded">
                        Not Installed
                      </span>
                    )}
                    <Button
                      size="sm"
                      className={`text-xs ${
                        wallet.installed 
                          ? 'bg-blue-gradient hover:from-blue-600 hover:to-blue-700' 
                          : 'bg-gray-600 hover:bg-gray-700'
                      }`}
                      onClick={(e) => {
                        e.stopPropagation();
                        wallet.connect();
                      }}
                      disabled={connecting}
                    >
                      {wallet.installed ? 'Connect' : 'Install'}
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        <div className="mt-6 text-center">
          <p className="text-xs text-gray-400">
            New to Solana wallets?{' '}
            <a 
              href="https://docs.solana.com/wallet-guide" 
              target="_blank" 
              rel="noopener noreferrer"
              className="text-blue-primary hover:underline"
            >
              Learn more
            </a>
          </p>
        </div>
      </DialogContent>
    </Dialog>
  );
}

