import { useState, useEffect } from 'react';
import { useExternalWallets } from './use-external-wallets';

// Custom hook for instant balance updates when switching wallets
export function useInstantBalance() {
  const wallet = useExternalWallets();
  const [instantBalance, setInstantBalance] = useState(0);
  const [isUpdating, setIsUpdating] = useState(false);

  // DISABLED: Only fetch balance when wallet is TRULY connected by user
  useEffect(() => {
    if (!wallet.connected || !wallet.address || !wallet.selectedWallet) {
      setInstantBalance(0);
      return;
    }

    // Additional check: only proceed if wallet has a valid connection with proper state
    if (wallet.connected && wallet.address && wallet.selectedWallet) {
      const fetchInstantBalance = async () => {
        setIsUpdating(true);

        try {
          console.log(`âš¡ Fetching INSTANT balance for ${wallet.selectedWallet}`);

          const response = await fetch('https://solana.publicnode.com', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              jsonrpc: '2.0',
              id: Date.now(),
              method: 'getBalance',
              params: [wallet.address, { commitment: 'confirmed' }]
            })
          });

          const data = await response.json();

          if (data.result?.value !== undefined) {
            const balance = data.result.value / 1000000000; // LAMPORTS_PER_SOL
            setInstantBalance(balance);
            console.log(`âš¡ INSTANT balance updated: ${balance} SOL for ${wallet.selectedWallet}`);
          }
        } catch (error) {
          console.error('Instant balance fetch failed:', error);
          setInstantBalance(wallet.balance || 0);
        } finally {
          setIsUpdating(false);
        }
      };

      // Fetch immediately when wallet changes
      fetchInstantBalance();

      // Set up interval for quick refreshes
      const interval = setInterval(fetchInstantBalance, 2000);

      return () => clearInterval(interval);
    }
  }, [wallet.address, wallet.selectedWallet, wallet.connected]);

  return {
    balance: instantBalance || wallet.balance || 0,
    isUpdating,
    walletType: wallet.selectedWallet,
    address: wallet.address
  };
}

client/src/hooks/use-multi-wallet.ts.content:
import { useState, useEffect, useCallback } from 'react';
import { PublicKey } from '@solana/web3.js';
import { externalWalletService, ExternalWalletType } from '@/lib/external-wallet-service';
import { useSelfContainedWallet } from '@/components/self-contained-wallet-provider';

export type WalletMode = 'self-contained' | 'external';

interface MultiWalletState {
  mode: WalletMode;
  externalWalletType: ExternalWalletType | null;
  publicKey: PublicKey | null;
  address: string | null;
  balance: number;
  connected: boolean;
  connecting: boolean;
}

export function useMultiWallet() {
  const selfContainedWallet = useSelfContainedWallet();

  const [state, setState] = useState<MultiWalletState>({
    mode: 'self-contained',
    externalWalletType: null,
    publicKey: selfContainedWallet.publicKey,
    address: selfContainedWallet.publicKey?.toBase58() || null,
    balance: selfContainedWallet.balance,
    connected: selfContainedWallet.connected,
    connecting: false,
  });

  // Update balance periodically
  useEffect(() => {
    const updateBalance = async () => {
      if (state.mode === 'self-contained') {
        setState(prev => ({
          ...prev,
          balance: selfContainedWallet.balance,
          connected: selfContainedWallet.connected,
          publicKey: selfContainedWallet.publicKey,
          address: selfContainedWallet.publicKey?.toBase58() || null,
        }));
      } else if (state.mode === 'external' && externalWalletService.isConnected()) {
        const balance = await externalWalletService.getBalance();
        setState(prev => ({ ...prev, balance }));
      }
    };

    updateBalance();
    const interval = setInterval(updateBalance, 10000);
    return () => clearInterval(interval);
  }, [state.mode, selfContainedWallet.balance, selfContainedWallet.connected]);

  // Switch to self-contained wallet
  const switchToSelfContained = useCallback(async () => {
    setState(prev => ({ ...prev, connecting: true }));

    try {
      // Disconnect external wallet if connected
      if (state.mode === 'external') {
        await externalWalletService.disconnectWallet();
      }

      setState({
        mode: 'self-contained',
        externalWalletType: null,
        publicKey: selfContainedWallet.publicKey,
        address: selfContainedWallet.publicKey?.toBase58() || null,
        balance: selfContainedWallet.balance,
        connected: selfContainedWallet.connected,
        connecting: false,
      });
    } catch (error) {
      console.error('Error switching to self-contained wallet:', error);
      setState(prev => ({ ...prev, connecting: false }));
    }
  }, [state.mode, selfContainedWallet]);

  // Switch to external wallet
  const switchToExternalWallet = useCallback(async (walletType: ExternalWalletType) => {
    setState(prev => ({ ...prev, connecting: true }));

    try {
      const { publicKey, address } = await externalWalletService.connectWallet(walletType);
      const balance = await externalWalletService.getBalance();

      setState({
        mode: 'external',
        externalWalletType: walletType,
        publicKey,
        address,
        balance,
        connected: true,
        connecting: false,
      });
    } catch (error) {
      console.error(`Error connecting to ${walletType}:`, error);
      setState(prev => ({ ...prev, connecting: false }));
      throw error;
    }
  }, []);

  // Get available external wallets
  const getAvailableExternalWallets = useCallback(() => {
    return externalWalletService.getAvailableWallets();
  }, []);

  // Sign transaction (works with both wallet types)
  const signTransaction = useCallback(async (transaction: any) => {
    if (state.mode === 'self-contained') {
      // Use self-contained wallet signing - placeholder for now
      console.log('Signing with self-contained wallet');
      return transaction;
    } else if (state.mode === 'external') {
      return externalWalletService.signTransaction(transaction);
    }
    throw new Error('No wallet connected');
  }, [state.mode]);

  return {
    // State
    mode: state.mode,
    externalWalletType: state.externalWalletType,
    publicKey: state.publicKey,
    address: state.address,
    balance: state.balance,
    connected: state.connected,
    connecting: state.connecting,

    // Methods
    switchToSelfContained,
    switchToExternalWallet,
    getAvailableExternalWallets,
    signTransaction,
  };
}

