import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Coins, Trophy, BookOpen, Zap } from 'lucide-react';
import { useExternalWallets } from '@/hooks/use-external-wallets';
import { xpService, type LearningModule, type XPData } from '@/services/xp-service';
import goldLogoPath from '@assets/k1xiYLna_400x400-removebg-preview_1754275575442.png';

export function LearnXPTab() {
  const { connected, address } = useExternalWallets();
  const [xpData, setXpData] = useState<XPData>({ totalXP: 0, availableXP: 0, exchangedGOLD: 0 });
  const [modules, setModules] = useState<LearningModule[]>([]);
  const [loading, setLoading] = useState(false);
  const [exchanging, setExchanging] = useState(false);

  useEffect(() => {
    if (connected && address) {
      loadUserData();
    }
  }, [connected, address]);

  const loadUserData = async () => {
    if (!address) return;

    setLoading(true);
    try {
      const [xp, progress] = await Promise.all([
        xpService.getUserXP(address),
        xpService.getLearningProgress(address)
      ]);

      setXpData(xp);
      setModules(progress);
    } catch (error) {
      console.error('Failed to load user data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCompleteModule = async (moduleId: string) => {
    if (!address) return;

    setLoading(true);
    try {
      const result = await xpService.completeModule(address, moduleId);

      // Update XP data
      setXpData(prev => ({
        ...prev,
        totalXP: result.newTotal,
        availableXP: prev.availableXP + result.xpEarned
      }));

      // Update module completion status
      setModules(prev => prev.map(module => 
        module.id === moduleId 
          ? { ...module, completed: true }
          : module
      ));

    } catch (error) {
      console.error('Failed to complete module:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleExchangeXP = async () => {
    if (!address) return;

    setExchanging(true);
    try {
      // Mock wallet object for exchange
      const mockWallet = { publicKey: address };
      const result = await xpService.exchangeXPForGOLD(address, mockWallet);

      // Update XP data
      await loadUserData();

    } catch (error) {
      console.error('XP exchange failed:', error);
    } finally {
      setExchanging(false);
    }
  };

  const canExchange = xpData.availableXP >= xpService.getXPToGoldRate();
  const goldFromXP = xpService.calculateGoldFromXP(xpData.availableXP);

  if (!connected) {
    return (
      <Card className="w-full max-w-md mx-auto">
        <CardContent className="flex flex-col items-center justify-center py-8">
          <BookOpen className="h-12 w-12 text-muted-foreground mb-4" />
          <p className="text-center text-muted-foreground">
            Connect your wallet to start earning XP from learning activities
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* XP Overview */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Zap className="h-5 w-5 text-yellow-500" />
            <span>Your XP Progress</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-3 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-500">{xpData.totalXP}</div>
              <div className="text-sm text-muted-foreground">Total XP</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-green-500">{xpData.availableXP}</div>
              <div className="text-sm text-muted-foreground">Available XP</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-yellow-500">{xpData.exchangedGOLD}</div>
              <div className="text-sm text-muted-foreground">GOLD Earned</div>
            </div>
          </div>

          {/* Exchange Progress */}
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Progress to next GOLD</span>
              <span>{xpData.availableXP % 1000}/1000 XP</span>
            </div>
            <Progress value={(xpData.availableXP % 1000) / 10} className="h-2" />
          </div>

          {/* Exchange Button */}
          {canExchange && (
            <Button 
              onClick={handleExchangeXP}
              disabled={exchanging}
              className="w-full bg-gradient-to-r from-yellow-400 to-yellow-600 hover:from-yellow-500 hover:to-yellow-700 text-white"
            >
              <img 
                src={goldLogoPath} 
                alt="GOLD" 
                className="w-5 h-5 mr-2"
              />
              {exchanging ? 'Exchanging...' : `Exchange ${goldFromXP} GOLD (${goldFromXP * 1000} XP)`}
            </Button>
          )}
        </CardContent>
      </Card>

      {/* Learning Modules */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <BookOpen className="h-5 w-5 text-blue-500" />
            <span>Learning Modules</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {modules.map((module) => (
            <div key={module.id} className="border rounded-lg p-4 space-y-3">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <h3 className="font-semibold">{module.title}</h3>
                  <p className="text-sm text-muted-foreground">{module.description}</p>
                </div>
                <div className="flex items-center space-x-2">
                  <Badge variant={module.completed ? "default" : "secondary"}>
                    {module.xpReward} XP
                  </Badge>
                  {module.completed && (
                    <Trophy className="h-4 w-4 text-yellow-500" />
                  )}
                </div>
              </div>

              {!module.completed && (
                <Button 
                  onClick={() => handleCompleteModule(module.id)}
                  disabled={loading}
                  size="sm"
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white"
                >
                  {loading ? 'Completing...' : 'Complete Module'}
                </Button>
              )}

              {module.completed && (
                <div className="flex items-center space-x-2 text-green-600">
                  <Trophy className="h-4 w-4" />
                  <span className="text-sm font-medium">Completed</span>
                </div>
              )}
            </div>
          ))}
        </CardContent>
      </Card>

      {/* XP Exchange Info */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Coins className="h-5 w-5 text-yellow-500" />
            <span>XP Exchange</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            <div className="flex items-center justify-between p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
              <div className="flex items-center space-x-2">
                <Zap className="h-5 w-5 text-yellow-500" />
                <span className="font-medium">1000 XP</span>
              </div>
              <div className="text-xl">â†’</div>
              <div className="flex items-center space-x-2">
                <img 
                  src={goldLogoPath} 
                  alt="GOLD" 
                  className="w-5 h-5"
                />
                <span className="font-medium">1 GOLD</span>
              </div>
            </div>
            <p className="text-sm text-muted-foreground">
              Complete learning modules to earn XP, then exchange for GOLD tokens. 
              Each module rewards you with XP based on its complexity and length.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/services/xp-service.ts.content:
import { goldTokenService } from './gold-token-service';

export interface XPData {
  totalXP: number;
  availableXP: number;
  exchangedGOLD: number;
}

export interface LearningModule {
  id: string;
  title: string;
  description: string;
  xpReward: number;
  completed: boolean;
}

export class XPService {
  private readonly XP_TO_GOLD_RATE = 1000; // 1000 XP = 1 GOLD

  // Learning modules with XP rewards
  private modules: LearningModule[] = [
    {
      id: 'blockchain_basics',
      title: 'Blockchain Fundamentals',
      description: 'Learn the basics of blockchain technology',
      xpReward: 150,
      completed: false
    },
    {
      id: 'solana_intro',
      title: 'Introduction to Solana',
      description: 'Understand Solana blockchain architecture',
      xpReward: 200,
      completed: false
    },
    {
      id: 'defi_concepts',
      title: 'DeFi Concepts',
      description: 'Master decentralized finance principles',
      xpReward: 250,
      completed: false
    },
    {
      id: 'wallet_security',
      title: 'Wallet Security',
      description: 'Learn how to secure your crypto wallet',
      xpReward: 175,
      completed: false
    },
    {
      id: 'trading_strategies',
      title: 'Trading Strategies',
      description: 'Understand crypto trading fundamentals',
      xpReward: 300,
      completed: false
    },
    {
      id: 'dao_governance',
      title: 'DAO & Governance',
      description: 'Learn about decentralized organizations',
      xpReward: 225,
      completed: false
    }
  ];

  async getUserXP(walletAddress: string): Promise<XPData> {
    try {
      const response = await fetch(`/api/xp/${walletAddress}`);
      if (response.ok) {
        return await response.json();
      }

      // Return default XP data if user not found
      return {
        totalXP: 0,
        availableXP: 0,
        exchangedGOLD: 0
      };
    } catch (error) {
      console.error('Failed to fetch user XP:', error);
      return {
        totalXP: 0,
        availableXP: 0,
        exchangedGOLD: 0
      };
    }
  }

  async completeModule(walletAddress: string, moduleId: string): Promise<{ xpEarned: number; newTotal: number }> {
    const module = this.modules.find(m => m.id === moduleId);
    if (!module) {
      throw new Error('Module not found');
    }

    try {
      const response = await fetch('/api/xp/complete-module', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          walletAddress,
          moduleId,
          xpEarned: module.xpReward
        })
      });

      if (!response.ok) {
        throw new Error('Failed to complete module');
      }

      const result = await response.json();
      console.log(`ðŸŽ“ Module "${module.title}" completed! Earned ${module.xpReward} XP`);
      console.log(`ðŸ’Ž Total XP: ${result.totalXP}, Available for exchange: ${result.availableXP}`);

      return {
        xpEarned: module.xpReward,
        newTotal: result.totalXP
      };
    } catch (error) {
      console.error('Failed to complete module:', error);
      throw error;
    }
  }

  async exchangeXPForGOLD(walletAddress: string, wallet: any): Promise<{ success: boolean; goldReceived: number; transactionId: string }> {
    const userXP = await this.getUserXP(walletAddress);

    if (userXP.availableXP < this.XP_TO_GOLD_RATE) {
      throw new Error(`Need ${this.XP_TO_GOLD_RATE} XP to exchange for 1 GOLD. You have ${userXP.availableXP} XP.`);
    }

    const goldAmount = Math.floor(userXP.availableXP / this.XP_TO_GOLD_RATE);
    const xpToExchange = goldAmount * this.XP_TO_GOLD_RATE;

    try {
      // Mint GOLD tokens to user's wallet
      const transactionId = await goldTokenService.mintGOLD(walletAddress, goldAmount, wallet);

      // Record the exchange in backend
      const response = await fetch('/api/xp/exchange', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          walletAddress,
          xpAmount: xpToExchange,
          goldAmount,
          transactionId
        })
      });

      if (!response.ok) {
        throw new Error('Failed to record XP exchange');
      }

      console.log(`ðŸ† Exchanged ${xpToExchange} XP for ${goldAmount} GOLD tokens!`);
      console.log(`ðŸ”— GOLD Mint Transaction:`, transactionId);

      return {
        success: true,
        goldReceived: goldAmount,
        transactionId
      };
    } catch (error) {
      console.error('XP to GOLD exchange failed:', error);
      throw error;
    }
  }

  getAvailableModules(): LearningModule[] {
    return this.modules;
  }

  async getLearningProgress(walletAddress: string): Promise<LearningModule[]> {
    try {
      const response = await fetch(`/api/xp/progress/${walletAddress}`);
      if (response.ok) {
        const progress = await response.json();

        // Update modules with completion status
        return this.modules.map(module => ({
          ...module,
          completed: progress.some((p: any) => p.moduleId === module.id && p.completed)
        }));
      }

      return this.modules;
    } catch (error) {
      console.error('Failed to fetch learning progress:', error);
      return this.modules;
    }
  }

  getXPToGoldRate(): number {
    return this.XP_TO_GOLD_RATE;
  }

  calculateGoldFromXP(xp: number): number {
    return Math.floor(xp / this.XP_TO_GOLD_RATE);
  }
}

export const xpService = new XPService();

