auto-deploy-vercel.js.content:
#!/usr/bin/env node

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('ðŸš€ Starting automatic Vercel deployment...');

// Check if vercel.json exists
if (!fs.existsSync('vercel.json')) {
  console.error('âŒ vercel.json not found!');
  process.exit(1);
}

try {
  // Install Vercel CLI if not installed
  console.log('ðŸ“¦ Checking Vercel CLI...');
  try {
    execSync('vercel --version', { stdio: 'pipe' });
    console.log('âœ… Vercel CLI already installed');
  } catch {
    console.log('ðŸ“¥ Installing Vercel CLI...');
    execSync('npm install -g vercel', { stdio: 'inherit' });
  }

  // Build the project
  console.log('ðŸ”¨ Building project...');
  execSync('npm run build', { stdio: 'inherit' });

  // Deploy to Vercel
  console.log('ðŸš€ Deploying to Vercel...');
  const deployOutput = execSync('vercel --prod --yes', { encoding: 'utf8' });

  // Extract URL from output
  const urlMatch = deployOutput.match(/https:\/\/[^\s]+/);
  if (urlMatch) {
    const deployUrl = urlMatch[0];
    console.log('\nðŸŽ‰ Deployment successful!');
    console.log('ðŸ”— Live URL:', deployUrl);

    // Save URL to file
    fs.writeFileSync('vercel-url.txt', deployUrl);
    console.log('ðŸ’¾ URL saved to vercel-url.txt');

    // Update README with live URL
    const readmePath = 'README.md';
    if (fs.existsSync(readmePath)) {
      let readme = fs.readFileSync(readmePath, 'utf8');
      const urlSection = `\n\n## ðŸŒ Live Demo\n[View Live Application](${deployUrl})\n`;

      if (readme.includes('## ðŸŒ Live Demo')) {
        readme = readme.replace(/## ðŸŒ Live Demo[\s\S]*?(?=\n##|$)/, urlSection.trim());
      } else {
        readme += urlSection;
      }

      fs.writeFileSync(readmePath, readme);
      console.log('ðŸ“ README.md updated with live URL');
    }

  } else {
    console.log('âš ï¸ Could not extract URL from deployment output');
    console.log('Deployment output:', deployOutput);
  }

} catch (error) {
  console.error('âŒ Deployment failed:', error.message);
  process.exit(1);
}

console.log('\nâœ¨ Auto-deployment completed!');

