import { useState, useEffect, useCallback } from 'react';
import { goldTokenService } from '../services/gold-token-service';
import { WalletStateManager } from '../lib/wallet-state';

export interface GoldBalanceState {
  balance: number;
  stakedBalance: number;
  totalValue: number;
  isLoading: boolean;
  error: string | null;
}

export function useGoldBalance() {
  const [goldState, setGoldState] = useState<GoldBalanceState>({
    balance: 0,
    stakedBalance: 0,
    totalValue: 0,
    isLoading: false,
    error: null
  });

  const walletState = WalletStateManager.getState();

  // Update GOLD balances
  const updateGoldBalances = useCallback(async () => {
    if (!walletState.connected || !walletState.address) {
      setGoldState({
        balance: 0,
        stakedBalance: 0,
        totalValue: 0,
        isLoading: false,
        error: null
      });
      return;
    }

    setGoldState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      console.log('ðŸª™ Fetching GOLD balances for wallet:', walletState.address);

      // First check transaction history for local balance tracking
      let balance = 0;
      let stakedBalance = 0;

      // Import transaction history dynamically to avoid circular dependency
      const { transactionHistory } = await import('../lib/transaction-history');

      // Set current wallet in transaction history
      transactionHistory.setCurrentWallet(walletState.address);

      const historyBalance = transactionHistory.getGoldBalance();
      const historyStaked = transactionHistory.getStakedGoldBalance();

      if (historyBalance > 0 || historyStaked > 0) {
        balance = historyBalance;
        stakedBalance = historyStaked;
        console.log(`âœ… Using GOLD balances from transaction history: ${balance} GOLD, ${stakedBalance} staked`);
      } else {
        // Fetch from blockchain as fallback
        [balance, stakedBalance] = await Promise.all([
          goldTokenService.getGoldBalance(walletState.address),
          goldTokenService.getStakedGoldBalance(walletState.address)
        ]);
        console.log(`âœ… GOLD balances from blockchain: ${balance} GOLD, ${stakedBalance} staked`);
      }

      const totalValue = (balance + stakedBalance) * 20; // $20 per GOLD

      setGoldState({
        balance,
        stakedBalance,
        totalValue,
        isLoading: false,
        error: null
      });

    } catch (error: any) {
      console.error('Failed to fetch GOLD balances:', error);
      setGoldState(prev => ({
        ...prev,
        isLoading: false,
        error: error.message || 'Failed to fetch GOLD balances'
      }));
    }
  }, [walletState.connected, walletState.address]);

  // Transfer GOLD tokens
  const transferGold = useCallback(async (toAddress: string, amount: number) => {
    if (!walletState.connected) {
      throw new Error('Wallet not connected');
    }

    // Get wallet adapter for signing
    let walletAdapter: any = null;
    switch (walletState.selectedWallet) {
      case 'phantom':
        walletAdapter = (window as any).solana;
        break;
      case 'solflare':
        walletAdapter = (window as any).solflare;
        break;
      case 'backpack':
        walletAdapter = (window as any).backpack;
        break;
      case 'trust':
        walletAdapter = (window as any).trustwallet || (window as any).trust;
        break;
    }

    if (!walletAdapter) {
      throw new Error('Wallet adapter not found');
    }

    const signature = await goldTokenService.transferGold(walletAdapter, toAddress, amount);

    // Refresh balances after transfer
    setTimeout(updateGoldBalances, 1000);

    return signature;
  }, [walletState.connected, walletState.selectedWallet, updateGoldBalances]);

  // Stake GOLD tokens
  const stakeGold = useCallback(async (amount: number) => {
    if (!walletState.connected) {
      throw new Error('Wallet not connected');
    }

    let walletAdapter: any = null;
    switch (walletState.selectedWallet) {
      case 'phantom':
        walletAdapter = (window as any).solana;
        break;
      case 'solflare':
        walletAdapter = (window as any).solflare;
        break;
      case 'backpack':
        walletAdapter = (window as any).backpack;
        break;
      case 'trust':
        walletAdapter = (window as any).trustwallet || (window as any).trust;
        break;
    }

    if (!walletAdapter) {
      throw new Error('Wallet adapter not found');
    }

    const signature = await goldTokenService.stakeGold(walletAdapter, amount);

    // Refresh balances after staking
    setTimeout(updateGoldBalances, 1000);

    return signature;
  }, [walletState.connected, walletState.selectedWallet, updateGoldBalances]);

  // Unstake GOLD tokens
  const unstakeGold = useCallback(async (amount: number) => {
    if (!walletState.connected) {
      throw new Error('Wallet not connected');
    }

    let walletAdapter: any = null;
    switch (walletState.selectedWallet) {
      case 'phantom':
        walletAdapter = (window as any).solana;
        break;
      case 'solflare':
        walletAdapter = (window as any).solflare;
        break;
      case 'backpack':
        walletAdapter = (window as any).backpack;
        break;
      case 'trust':
        walletAdapter = (window as any).trustwallet || (window as any).trust;
        break;
    }

    if (!walletAdapter) {
      throw new Error('Wallet adapter not found');
    }

    const signature = await goldTokenService.unstakeGold(walletAdapter, amount);

    // Refresh balances after unstaking
    setTimeout(updateGoldBalances, 1000);

    return signature;
  }, [walletState.connected, walletState.selectedWallet, updateGoldBalances]);

  // Swap SOL for GOLD
  const swapSolForGold = useCallback(async (solAmount: number) => {
    if (!walletState.connected) {
      throw new Error('Wallet not connected');
    }

    let walletAdapter: any = null;
    switch (walletState.selectedWallet) {
      case 'phantom':
        walletAdapter = (window as any).solana;
        break;
      case 'solflare':
        walletAdapter = (window as any).solflare;
        break;
      case 'backpack':
        walletAdapter = (window as any).backpack;
        break;
      case 'trust':
        walletAdapter = (window as any).trustwallet || (window as any).trust;
        break;
    }

    if (!walletAdapter) {
      throw new Error('Wallet adapter not found');
    }

    const signature = await goldTokenService.swapSolForGold(walletAdapter, solAmount);

    // Refresh balances after swap
    setTimeout(updateGoldBalances, 2000);

    return signature;
  }, [walletState.connected, walletState.selectedWallet, updateGoldBalances]);

  // Auto-update balances when wallet state changes
  useEffect(() => {
    updateGoldBalances();

    // Set up periodic updates for connected wallet
    if (walletState.connected) {
      const interval = setInterval(updateGoldBalances, 30000); // Every 30 seconds
      return () => clearInterval(interval);
    }
  }, [updateGoldBalances, walletState.connected]);

  return {
    ...goldState,
    transferGold,
    stakeGold,
    unstakeGold,
    swapSolForGold,
    refreshBalances: updateGoldBalances,
    stakingInfo: goldTokenService.getStakingInfo()
  };
}

client/src/lib/transaction-history.ts.content:
import { SwapMetadata } from './swap-service';

export interface TransactionRecord {
  id: string;
  type: 'swap' | 'send' | 'stake' | 'unstake';
  signature: string;
  timestamp: number;
  fromToken?: string;
  toToken?: string;
  fromAmount?: number;
  toAmount?: number;
  recipientAddress?: string;
  status: 'pending' | 'confirmed' | 'failed';
  txUrl: string;
}

interface WalletTransactionData {
  transactions: TransactionRecord[];
  goldBalance: number;
  stakedGoldBalance: number;
}

class TransactionHistoryManager {
  private static instance: TransactionHistoryManager;
  private currentWallet: string | null = null;
  private walletData: Map<string, WalletTransactionData> = new Map();

  static getInstance(): TransactionHistoryManager {
    if (!TransactionHistoryManager.instance) {
      TransactionHistoryManager.instance = new TransactionHistoryManager();
    }
    return TransactionHistoryManager.instance;
  }

  // Set current active wallet
  setCurrentWallet(walletAddress: string | null): void {
    this.currentWallet = walletAddress;
    if (walletAddress) {
      this.loadWalletData(walletAddress);
      console.log(`ðŸ“± Switched to wallet: ${walletAddress.slice(0, 8)}...${walletAddress.slice(-4)}`);
    } else {
      console.log(`ðŸ“± No wallet connected`);
    }
  }

  // Load wallet data from localStorage
  private loadWalletData(walletAddress: string): void {
    if (this.walletData.has(walletAddress)) return;

    try {
      const stored = localStorage.getItem(`goldium_wallet_${walletAddress}`);
      if (stored) {
        const data: WalletTransactionData = JSON.parse(stored);
        this.walletData.set(walletAddress, data);
        console.log(`ðŸ“š Loaded ${data.transactions.length} transactions for wallet ${walletAddress.slice(0, 8)}...`);
      } else {
        // Initialize empty wallet data
        this.walletData.set(walletAddress, {
          transactions: [],
          goldBalance: 0,
          stakedGoldBalance: 0
        });
        console.log(`ðŸ†• Initialized new wallet data for ${walletAddress.slice(0, 8)}...`);
      }
    } catch (error) {
      console.error('Failed to load wallet data:', error);
      this.walletData.set(walletAddress, {
        transactions: [],
        goldBalance: 0,
        stakedGoldBalance: 0
      });
    }
  }

  // Save wallet data to localStorage
  private saveWalletData(walletAddress: string): void {
    const data = this.walletData.get(walletAddress);
    if (!data) return;

    try {
      localStorage.setItem(`goldium_wallet_${walletAddress}`, JSON.stringify(data));
      console.log(`ðŸ’¾ Saved wallet data for ${walletAddress.slice(0, 8)}... (${data.transactions.length} transactions)`);
    } catch (error) {
      console.error('Failed to save wallet data:', error);
    }
  }

  // Get current wallet data
  private getCurrentWalletData(): WalletTransactionData | null {
    if (!this.currentWallet) return null;
    return this.walletData.get(this.currentWallet) || null;
  }

  // Add transaction to current wallet's history
  addTransaction(transaction: Omit<TransactionRecord, 'id' | 'txUrl'>): void {
    if (!this.currentWallet) {
      console.warn('âš ï¸ No wallet connected - transaction not saved');
      return;
    }

    const walletData = this.getCurrentWalletData();
    if (!walletData) return;

    const id = `tx_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    const txUrl = `https://solscan.io/tx/${transaction.signature}`;

    const fullTransaction: TransactionRecord = {
      ...transaction,
      id,
      txUrl
    };

    walletData.transactions.unshift(fullTransaction); // Add to beginning for newest first

    // Update GOLD balances based on transaction
    this.updateBalancesFromTransaction(fullTransaction, walletData);

    // Save to localStorage
    this.saveWalletData(this.currentWallet);

    console.log(`ðŸ“‹ Transaction added to wallet ${this.currentWallet.slice(0, 8)}...: ${transaction.type} - ${transaction.signature}`);
  }

  // Update GOLD balances based on transaction for specific wallet
  private updateBalancesFromTransaction(transaction: TransactionRecord, walletData: WalletTransactionData): void {
    if (transaction.status !== 'confirmed') return;

    switch (transaction.type) {
      case 'swap':
        if (transaction.toToken === 'GOLD' && transaction.toAmount) {
          walletData.goldBalance += transaction.toAmount;
          console.log(`âœ… GOLD balance increased by ${transaction.toAmount} from swap (Total: ${walletData.goldBalance})`);
        }
        break;

      case 'send':
        if (transaction.fromToken === 'GOLD' && transaction.fromAmount) {
          walletData.goldBalance -= transaction.fromAmount;
          console.log(`ðŸ“¤ GOLD balance decreased by ${transaction.fromAmount} from send (Total: ${walletData.goldBalance})`);
        }
        break;

      case 'stake':
        if (transaction.fromToken === 'GOLD' && transaction.fromAmount) {
          walletData.goldBalance -= transaction.fromAmount;
          walletData.stakedGoldBalance += transaction.fromAmount;
          console.log(`ðŸ”’ GOLD staked: ${transaction.fromAmount} (Balance: ${walletData.goldBalance}, Staked: ${walletData.stakedGoldBalance})`);
        }
        break;

      case 'unstake':
        if (transaction.toToken === 'GOLD' && transaction.toAmount) {
          walletData.stakedGoldBalance -= transaction.toAmount;
          walletData.goldBalance += transaction.toAmount;
          console.log(`ðŸ”“ GOLD unstaked: ${transaction.toAmount} (Balance: ${walletData.goldBalance}, Staked: ${walletData.stakedGoldBalance})`);
        }
        break;
    }
  }

  // Get all transactions for current wallet
  getTransactions(): TransactionRecord[] {
    const walletData = this.getCurrentWalletData();
    return walletData ? [...walletData.transactions] : [];
  }

  // Get GOLD balance for current wallet
  getGoldBalance(): number {
    const walletData = this.getCurrentWalletData();
    return walletData ? walletData.goldBalance : 0;
  }

  // Get staked GOLD balance for current wallet
  getStakedGoldBalance(): number {
    const walletData = this.getCurrentWalletData();
    return walletData ? walletData.stakedGoldBalance : 0;
  }

  // Update transaction status for current wallet
  updateTransactionStatus(signature: string, status: 'confirmed' | 'failed'): void {
    if (!this.currentWallet) return;

    const walletData = this.getCurrentWalletData();
    if (!walletData) return;

    const transaction = walletData.transactions.find(tx => tx.signature === signature);
    if (transaction) {
      transaction.status = status;
      if (status === 'confirmed') {
        this.updateBalancesFromTransaction(transaction, walletData);
      }

      // Save updated data
      this.saveWalletData(this.currentWallet);
      console.log(`ðŸ“ Transaction ${signature} status updated to: ${status}`);
    }
  }

  // Clear history for current wallet (for testing/reset)
  clearHistory(): void {
    if (!this.currentWallet) return;

    const walletData = this.getCurrentWalletData();
    if (walletData) {
      walletData.transactions = [];
      walletData.goldBalance = 0;
      walletData.stakedGoldBalance = 0;
      this.saveWalletData(this.currentWallet);
      console.log(`ðŸ—‘ï¸ Transaction history cleared for wallet ${this.currentWallet.slice(0, 8)}...`);
    }
  }

  // Get transactions by type for current wallet
  getTransactionsByType(type: TransactionRecord['type']): TransactionRecord[] {
    const walletData = this.getCurrentWalletData();
    return walletData ? walletData.transactions.filter((tx: TransactionRecord) => tx.type === type) : [];
  }

  // Get recent transactions (last 10) for current wallet
  getRecentTransactions(): TransactionRecord[] {
    const walletData = this.getCurrentWalletData();
    return walletData ? walletData.transactions.slice(0, 10) : [];
  }

  // Get all connected wallets
  getConnectedWallets(): string[] {
    return Array.from(this.walletData.keys());
  }

  // Get wallet stats
  getWalletStats(walletAddress: string): { transactionCount: number; goldBalance: number; stakedGold: number } | null {
    const data = this.walletData.get(walletAddress);
    if (!data) return null;

    return {
      transactionCount: data.transactions.length,
      goldBalance: data.goldBalance,
      stakedGold: data.stakedGoldBalance
    };
  }
}

export const transactionHistory = TransactionHistoryManager.getInstance();

client/src/lib/wallet-state.ts.content:
// Global wallet state manager to fix multiple instance issues
import { PublicKey } from '@solana/web3.js';
import type { SupportedWallet } from '@/hooks/use-external-wallets';

interface GlobalWalletState {
  connected: boolean;
  connecting: boolean;
  publicKey: PublicKey | null;
  address: string | null;
  balance: number;
  selectedWallet: SupportedWallet | null;
  lastUpdated: number;
}

let globalState: GlobalWalletState = {
  connected: false,
  connecting: false,
  publicKey: null,
  address: null,
  balance: 0,
  selectedWallet: null,
  lastUpdated: 0,
};

const listeners = new Set<() => void>();

export const WalletStateManager = {
  getState: () => ({ ...globalState }),

  setState: (newState: Partial<GlobalWalletState>) => {
    globalState = { ...globalState, ...newState, lastUpdated: Date.now() };
    console.log('ðŸ”„ Global wallet state updated:', globalState);
    listeners.forEach(listener => listener());
  },

  subscribe: (listener: () => void) => {
    listeners.add(listener);
    return () => listeners.delete(listener);
  },

  reset: () => {
    globalState = {
      connected: false,
      connecting: false,
      publicKey: null,
      address: null,
      balance: 0,
      selectedWallet: null,
      lastUpdated: 0,
    };
    listeners.forEach(listener => listener());
  }
};

client/src/components/games/crypto-guess.content:
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';

const CRYPTO_PRICES = [
  { name: 'Bitcoin', symbol: 'BTC', basePrice: 65000 },
  { name: 'Ethereum', symbol: 'ETH', basePrice: 3200 },
  { name: 'Solana', symbol: 'SOL', basePrice: 195 },
  { name: 'Cardano', symbol: 'ADA', basePrice: 0.45 },
  { name: 'Dogecoin', symbol: 'DOGE', basePrice: 0.08 }
];

export function CryptoGuessGame() {
  const [currentCrypto, setCurrentCrypto] = useState(CRYPTO_PRICES[0]);
  const [actualPrice, setActualPrice] = useState(0);
  const [guess, setGuess] = useState('');
  const [score, setScore] = useState(0);
  const [round, setRound] = useState(1);
  const [gameResult, setGameResult] = useState<'correct' | 'close' | 'wrong' | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const generatePrice = () => {
    // Add realistic price variation (Â±10%)
    const variation = (Math.random() - 0.5) * 0.2; // -10% to +10%
    return Math.round(currentCrypto.basePrice * (1 + variation) * 100) / 100;
  };

  const startNewRound = () => {
    const randomCrypto = CRYPTO_PRICES[Math.floor(Math.random() * CRYPTO_PRICES.length)];
    setCurrentCrypto(randomCrypto);
    setActualPrice(generatePrice());
    setGuess('');
    setGameResult(null);
    setIsPlaying(true);
  };

  const checkGuess = () => {
    const guessNum = parseFloat(guess);
    const difference = Math.abs(guessNum - actualPrice);
    const percentDiff = (difference / actualPrice) * 100;

    if (percentDiff <= 2) {
      setGameResult('correct');
      setScore(score + 10);
    } else if (percentDiff <= 10) {
      setGameResult('close');
      setScore(score + 5);
    } else {
      setGameResult('wrong');
    }

    setTimeout(() => {
      if (round < 5) {
        setRound(round + 1);
        startNewRound();
      } else {
        setIsPlaying(false);
      }
    }, 2000);
  };

  useEffect(() => {
    startNewRound();
  }, []);

  return (
    <Card className="bg-gradient-to-br from-purple-900/20 to-blue-900/20 border-purple-500/30">
      <CardHeader>
        <CardTitle className="text-xl text-center">Crypto Price Guess</CardTitle>
        <div className="flex justify-between text-sm">
          <span>Round: {round}/5</span>
          <span>Score: {score}</span>
        </div>
      </CardHeader>
