                <Badge variant={gameResult === 'correct' ? 'default' : gameResult === 'close' ? 'secondary' : 'destructive'}>
                  {gameResult === 'correct' && 'ðŸŽ‰ Perfect! +10 points'}
                  {gameResult === 'close' && 'ðŸ‘ Close! +5 points'}
                  {gameResult === 'wrong' && 'âŒ Not quite right!'}
                </Badge>
              </div>
            )}
